mapper.select:
select
from t_case_assign where assign_status !='0'
and create_time BETWEEN #{param.startDate,jdbcType=TIMESTAMP} and #{param.endDate,jdbcType=TIMESTAMP}
and create_time > #{param.startDate,jdbcType=TIMESTAMP}
and create_time < #{param.endDate,jdbcType=TIMESTAMP}
and assign_title like '%${param.title}%'
and assign_code like'%${param.code}%'
and case_type = #{param.caseType,jdbcType=VARCHAR}
and login_acct = #{param.loginAcct}
and main_seed_org like '%${param.mainSeedOrg}%'
and #{unitId} = { SELECT account_unit_id FROM t_pub_account_tab WHERE account_id = login_acct } and assign_status = '30'
AND assign_id NOT IN ( SELECT la.`assign_id` FROM t_issue_lawsuit la WHERE la.`lawsuit_status`!='10')
order by assign_status,create_time desc

select tca.assign_id, tca.assign_code, tca.assign_type, tca.assign_title, tca.approve_state_name, tca.approve_city_name, tca.approve_state, tca.approve_city, tca.approve_org, tca.case_create_time, tca.create_time, tca.case_code, tca.case_line, tca.case_cause, tca.case_type, tca.lawsuit_size, tca.lawsuit_money, tca.our_lawsuit_body, tca.our_lawsuit_body_name, tca.they_lawsuit_body, tca.our_lawsuit_status, tca.other_related, tca.third_person, tca.plaintiff, tca.defendant, tca.send_time, tca.plaintiff_request, tca.case_file, tca.assign_org, tca.main_seed_org, tca.main_seed_org_id, tca.assign_item, tca.assign_file, tca.sign_dept, tca.login_acct, tca.login_name, tca.org_name, tca.org_id, tca.dept_name, tca.dept_id, tca.mobile, tca.assign_status, tca.module_name from t_pub_org tpo right join t_case_assign tca on tpo.org_code = tca.main_seed_org_id
tca.assign_status in ('20', '30')
and tca.create_time > #{param.startDate,jdbcType=TIMESTAMP}
and tca.create_time < #{param.endDate,jdbcType=TIMESTAMP}
and tca.assign_title like '%${param.title}%'
and tca.assign_code like'%${param.code}%'
and tca.assign_org like'%${param.assignOrg}%'
and tca.case_type = #{param.caseType,jdbcType=VARCHAR}
and tca.main_seed_org like '%${param.mainSeedOrg}%'
and tpo.org_path like #{param.orgPath}
and tca.assign_status = #{param.assignStatus}
order by tca.assign_status,tca.create_time desc
